class LoginPresenterTests: XCTestCase {

    // MARK: - Mocks
    private let mockViewController = LoginViewControllerProtocolMock()

    // MARK: - Test lifecycle
    override func setUp() {
        
    }

    // MARK: - Tests
    func testPresentLogin() {
        let sut = createPresenter()
        sut.presentLogin()
//        Verify(mockViewController, .displayLogin())
    }
    
    func testPresentNotABleToLogin() {
        let sut = createPresenter()
