        sut.presentNotAbleToLogin(error: error)
//        Verify(mockViewController, .displayLoginError(error: .value(error)))
    }
    
    func testPresentSuccesfullLogin() {
        let sut = createPresenter()
        sut.presentSuccesfullLogin()
//        Verify(mockViewController, .succesfullUserLogin())
